import {useRef} from "react"
import {FaBars, FaTimes} from "react-icons/fa"
import "../Styles/main.css"
function NavBar() {
    const navRef = useRef();

    const showNavBar = () => {
        navRef.current.classList.toggle("responsive_nav")
    }

    return (
        <header>
            <nav ref={navRef}>
                <a href="/#">Home</a>
                <button className="navBttnClose" onClick={showNavBar}>
                    <FaTimes/>
                </button>
            </nav>
            <button className="navBttn" onClick={showNavBar}>
                <FaBars/>
            </button>
        </header>
    );
}

export default NavBar;
